import React,{useState} from 'react'
import ProductsItem from './ProductsItem'

const ProductsList = ({product, onsearch}) => {
    console.log(product)
    const [price, setPrice] = useState('')
    const [name, setName] = useState('')

    const handleSubmit = (e) => {
        e.preventDefault();
        onsearch(name, price)
    };

    const displayProducts = product.map(products => {
        var productimage = ""
        products.images.map(image => {
            productimage = image.image1
            return productimage
        })
        return <ProductsItem key = {products.id} id = {products.id} name = {products.name} description={products.description} image={products.image} price={products.price}/>
    })

  return (
    <div>
        <form onSubmit={handleSubmit}>
            <label htmlFor='name'>Name: </label>
            <select name='name' id='name' value={name} onChange={(e) => setName(e.target.value)}>
                <option value=''>Any</option>
                <option value='shirt'>Shirt</option>
                <option value='vest'>Vest</option>
                <option value='trouser'>Trouser</option>
                <option value='jacket'>Jacket</option>
            </select>
            <button className='bg-[#00df9a] w-[200px] rounded-md font-medium my-6 mx-auto py-3 text-black' type='submit'>
                <span>Search</span>
            </button>
        </form>
        <div className='grid grid-cols-1 lg:grid-cols-4 sm:grid-cols-2 md:grid-cols-3 gap-2'>
        {displayProducts}
      </div>
    </div>
  )
}

export default ProductsList
